$(function(){
	/*
	//This is a plugin for ThemeRoller for jQuery Mobile
	//It adds integration for Adobe Kuler so the user can browse color themes and drag colors onto elements
	//It adds an activation link and all markup and CSS necessary for the interface
	*/
	var kuler_on = 0;
	var search_query = 0;
	var total_pages = 0;
	var current_page = 0;
	
	//array of base64 encoded images
	var images = {
		"search": "data:image/png;base64,",
		"detail_bg": "data:image/png;base64,",
		"kuler_logo": "data:image/png;base64,",
		"kuler_logo2": "data:image/png;base64,",
		"kuler_close": "data:image/png;base64,",
		"larrow": "data:image/png;base64,",
		"rarrow": "data:image/png;base64,",
		"larrow_light": "data:image/png;base64,",
		"rarrow_light": "data:image/png;base64,",
		"star": "data:image/png;base64,",
		"star2": "data:image/png;base64,",
		"arrow_right": "data:image/png;base64,",
		"arrow_down": "data:image/png;base64,",
	};
	
	//Backup plan for hiding details panel
	$( "#kuler" ).mouseout(function() {
		$( ".kuler-details" ).hide();
	});
	
	//Adding kuler activation link
	var h2 = $( "#quickswatch h2" );
	h2.html( h2.html() + " or pick from the <img src=\"" + images["kuler_logo"] + "\" alt=\" \"/><span id=\"kuler-activation\">Adobe Kuler swatches<img src=\"" + images["arrow_down"] + "\" alt=\" \" /></span>" );
	
	$( "#kuler-activation" ).click(initialize_kuler);
	
	//This function adds necessary markup and CSS as well as 
	//setting up draggable events and paging
 	function initialize_kuler() {
		if(!kuler_on) {
			kuler_on = 1;
		
			$( "#quickswatch" ).hide();
			$( "head" ).append( "<style type=\"text/css\">\n\n\n			#kuler{\n				width: 580px;\n				background-color: #161616;\n				height: 100%;\n				float: left;\n				color: #fff;\n			}\n\n			#kuler .color-drag{\n				border: none;\n				width: 14px;\n				height: 14px;\n			}\n\n			#kuler #left-panel{\n				width: 100px;\n				height: 70px;\n				border-right: 1px solid #777;\n				padding: 10px;\n				float: left;\n			}\n\n			#kuler #left-panel>img{\n				width: 100%;\n			}\n\n			#kuler #left-panel #kuler-select{\n				margin-top: 20px;\n				margin-bottom: 2px;\n				width: 100px;\n			}\n\n			#kuler #left-panel #kuler-search{\n				width: 20px;\n				height: 20px;\n				background: transparent;\n				border: 1px solid #777;\n				background-image: url(" + images["search"] + ");\n				background-repeat: no-repeat;\n				background-position: center;\n				margin-left: 5px;\n				float: left;\n			}\n\n			#kuler #left-panel input{\n				width: 68px;\n				font-size: 10px;\n				float: left;\n			}\n\n			#kuler #kuler-search-query{\n				display: none;\n				margin-top: 1px;\n			}\n\n			#kuler #center-panel{\n				width: 393px;\n				height: 77px;\n				padding: 3px 10px 10px;\n				float: left;\n				border-right: 1px solid #777;\n			}\n\n			#kuler #center-panel .kuler-page{\n				display: none;\n				float: left;\n			}\n\n			#kuler #center-panel .kuler-page.active{\n				display: block;\n			}\n\n			#kuler #center-panel p{\n				margin-top: 0;\n				margin-bottom: 5px;\n				color: #fff;\n				width: 100%;\n				overflow: hidden;\n				font-size: 10px;\n				height: 10px;\n			}\n\n			#kuler #center-panel .kuler-theme{\n				margin: 10px 4px 0;\n				float: left;\n				width: 70px;\n			}\n\n			#kuler #center-panel #kuler-loader{\n				font-size: 12px; \n				width: 75px;				\n				height: 15px;\n				margin: 30px auto;\n			}\n\n			#kuler-loader img{\n				position: relative; \n				top: 3px;				\n				height: inherit;\n			}\n\n			#kuler .kuler-details{\n				display: none;\n				width: 148px;\n				padding: 10px;\n				position: absolute;\n				z-index: 1200;\n				border: 1px solid #777;\n				background-image: url(" + images["detail_bg"] + ");\n				font-size: 13px;\n			}\n\n			.kuler-details h5{\n				margin: 0;\n				font-size: 13px;\n			}\n\n			#kuler #center-panel .kuler-details p{\n				margin-top: 2px;\n				font-size: 12px;\n				color: #ddd;\n				height: 14px;\n			}\n\n			.kuler-details span{\n				color: #ddd;\n				font-size: 10px;\n			}\n\n			.kuler-details .detail-star{\n				width: 10px;\n				height: 10px;\n			}\n\n			.kuler-details .detail-footer{\n				margin-top: 10px;\n				border-top: 1px solid #777;\n				padding: 10px 0 0;\n				width: 148px;\n			}\n\n			.detail-footer a{\n				text-decoration: none;\n				color: #fff;\n				margin-right: 10px;\n			}\n\n			#kuler .kuler-theme .colors{\n				width: 70px;\n			}\n\n			#kuler #right-panel{\n				height: 70px;\n				width: 45px;\n				float: left;\n				font-size: 12px;\n			}\n\n			#kuler #kuler-close{\n				height: 15px;\n				padding: 5px 5px;\n				border-bottom: 1px solid #777;\n				text-align: center;\n				cursor: pointer;\n			}\n\n			#kuler #right-panel #kuler-pages>div{\n				margin-top: 10px;\n				text-align: center;\n			}\n\n			#kuler #right-panel #kuler-pages #kuler-back-page{\n				text-align: center;\n				width: 15px;\n				height: 15px;\n				margin: 7px 0px 0 8px;\n				float: left;\n				cursor: pointer;\n			}\n\n			#kuler #right-panel #kuler-pages #kuler-next-page{\n				text-align: center;\n				width: 15px;\n				height: 15px;\n				margin: 7px 0px 0 0px;\n				float: left;\n				cursor: pointer;\n			}</style>\n" );
			$( "#quickswatch" ).after( "<div id=\"kuler\">		<div id=\"left-panel\">		<img src=\"" + images["kuler_logo2"] + "\" alt=\" \" />		<select id=\"kuler-select\">			<option value=\"recent\">Newest</option>			<option value=\"popular\">Popular</option>			<option value=\"rating\">Highest Rated</option>			<option value=\"random\">Random</option>			<option value=\"tag\">Search Tags</option>			<option value=\"title\">Search Title</option>			<option value=\"hex\">Search Colors</option>		</select>		<div id=\"kuler-search-query\">			<input /><button id=\"kuler-search\"></button>		</div>	</div>		<div id=\"center-panel\">			</div>	<div id=\"right-panel\">		<div id=\"kuler-close\">			<img src=\"" + images["kuler_close"] + "\" />		</div>		<div id=\"kuler-pages\">			<div>							<br /><br />			</div>			<div id=\"kuler-back-page\"><img src=\"" + images["larrow"] + "\" alt=\" \" /></div>			<div id=\"kuler-next-page\"><img src=\"" + images["rarrow"] + "\" alt=\" \" /></div>		</div>	</div></div>" );
		
			$('#kuler-close').click(function(){
				$('#kuler').hide();
			})
		
			$( "#kuler-next-page" ).click(function() {
				var id = $( ".kuler-page.active" ).attr( "data-id" );
				var id = id.split( "-" )[1];
				var next = $( "[data-id=page-" + (parseInt(id) + 1) + "]" );
				if( next.html() ) {
					$( ".kuler-page.active" ).removeClass( "active" );
					next.addClass( "active" );
					$( "#kuler-back-page img" ).attr( "src", images["larrow_light"] );
					if( $("[data-id=page-" + (parseInt(id) + 2) + "]").html() === null ) {
						$( "#kuler-next-page img" ).attr( "src", images["rarrow"] );
					}
					current_page += 1;
					$( "#kuler-pages>div:first" ).html( "PAGE<br />" + current_page + "/" + total_pages );
				}
			});

			$( "#kuler-back-page" ).click(function() {
				var id = $( ".kuler-page.active" ).attr( "data-id" );
				var id = id.split( "-" )[1];
				var back = $( "[data-id=page-" + (parseInt(id) - 1) + "]" );
				if( back.html() ) {
					$( ".kuler-page.active" ).removeClass( "active" );
					back.addClass( "active" );
				
					$( "#kuler-next-page img" ).attr( "src", images["rarrow_light"] );
					if( $("[data-id=page-" + (parseInt(id) - 2) + "]").html() === null ) {
						$( "#kuler-back-page img" ).attr( "src", images["larrow"] );
					}
					current_page -= 1;
					$( "#kuler-pages>div:first" ).html( "PAGE<br />" + current_page + "/" + total_pages );
				}
			});

			$( "#kuler-search" ).submit(function(e) {
				e.preventDefault();
				e.stopPropagation();

				var search_by = $( "#kuler-search-by" ).val();
				get_theme( "search=true&timeSpan=30&searchQuery=" + search_by + ":" + $(this).find("input[type=text]").val() );
			});
		
			$( "#kuler-close" ).click(function() {
				$( "#kuler" ).hide();
				$( "#quickswatch" ).show();
			});
		
			$( "#kuler-select" ).bind("change keyup", function(e) {
				//on change we search with the new value
				if( e["type"] == "change" || (e["type"] == "keyup" && e.which == 13) ) {
					var val = $(this).val();
					if( $("#kuler-select [value=" + val + "]").text().indexOf("Search") == -1 ) {
						val = "list=true&listType=" + val + "&timeSpan=30";
						get_theme(val);
						if(search_query) {
							$( "#kuler-search-query" ).hide();
								setTimeout(function(){
								$( "#kuler-select" ).animate({
									marginTop: "20px"
								}, 320);
								search_query = 0;
							}, 200);
						}
					} else {
						//if we choose one that involves a search query we merely show the search field
						if( !search_query ) {
							$( "#kuler-select" ).animate({
								marginTop: "3px"
							}, 320, function(){
								$( "#kuler-search-query" ).fadeIn();
								$( "#kuler-search-query input" ).focus();
							})
							search_query = 1;
						}
					}
				}
			});
		
			$( "#kuler-search-query" ).keydown(function(e) {
				if( e.which == 13 ) {
					get_theme( "search=true&&timeSpan=30&searchQuery=" + $( "#kuler-select" ).val() + ":" + $("input", this).val() );
				}
			});
		
			$( "#kuler-search" ).click(function() {
				get_theme( "search=true&&timeSpan=30&searchQuery=" + $( "#kuler-select" ).val() + ":" + $(this).siblings("input").val() );
			});
		
			get_theme( "list=true&listType=popular&timeSpan=30" );
		} else {
			$( "#quickswatch" ).hide();
			$( "#kuler" ).show();
		}
	}
	
	function get_theme(data){		
		var kuler = $( "#kuler #center-panel" );
		
		$.ajax({
			url: "./kuler.php",
			//proper dataType would be xml but Chrome and Safari would not successfully call $(data) unless we used plain text
			dataType: "text",
			type: "post",
			data: data,
			mimeType: "text/xml",
			beforeSend: function() {
				kuler.find( "*" ).remove();
				$( "#kuler-pages>div:first" ).html( "<br /><br />" );
				kuler.html( "<div id=\"kuler-loader\"><img src=\"images/ajax-loader.gif\" alt=\" \" />&nbsp;&nbsp;Loading...</div>" );
			},
			success: function(data){
				kuler.html( "" )
				var xml = $(data);
				var count = 0;
				var page = "";
				var tmp = "";
				var page_count = 1;
				
				xml.find( "kuler\\:themeItem" ).each(function(){
					tmp = "";
					if( count == 0 ) {
						if( page_count == 1 ) {
							page = $( "<div class=\"kuler-page active\" data-id=\"page-1\"></div>" );
						} else {
							page = $( "<div class=\"kuler-page\" data-id=\"page-" + page_count + "\"></div>" );
						}
					}
					
					var $this = $( this );
					var colors = [];
					tmp += "<div class=\"kuler-theme\"><p class=\"kuler-title\" data-id=\"" + count + "\">" + $this.find( "kuler\\:themeTitle" ).text() + "</p>";
					tmp += "<div class=\"kuler-colors\">";
					$this.find( "kuler\\:swatch" ).each(function() {
						colors.push( $(this).find("kuler\\:swatchHexColor").text() );
						tmp += "<div class=\"color-drag\" style=\"background-color:#" + $(this).find( "kuler\\:swatchHexColor" ).text() + "\"></div>";
					});
					tmp += "</div><div class=\"kuler-details\" data-id=\"" + count + "\"><h5>" + $this.find( "kuler\\:themeTitle" ).text() + "</h5><p>by " + $this.find( "kuler\\:authorLabel" ).text() + "</p><div class=\"rating\">";
					var rating = parseInt( $this.find("kuler\\:themeRating").text() );
					for( var i = 0; i < rating; i++ ) {
						tmp += "<img src=\"" + images["star"] + "\" class=\"detail-star\" alt=\" \"/>";
					}
					for( ; i < 5; i++ ) {
						tmp += "<img src=\"" + images["star2"] + "\" class=\"detail-star\" alt=\" \"/>";
					}
					tmp += "<span>" + $this.find( "kuler\\:themeDownloadCount" ).text() + " downloads</span></div><div class=\"detail-footer\"><a target=\"_blank\" href=\"http://kuler.adobe.com/index.cfm#themeID/" + $this.find( "kuler\\:themeID" ).text() + "\">View on kuler.com</a><img src=\"" + images["arrow_right"] + "\" alt=\" \"/></div></div></div>";
				
					if( count < 10 ) {
						page.append( tmp );
					}
					if( count == 9 ) {
						page_count++;
						kuler.append( page );
						count = -1;
					}
					count++;
				});
				if( count > 0 && count < 9 ) {
					kuler.append(page);
					page_count++;
				}
				
				current_page = 1;
				total_pages = page_count - 1;
				if(total_pages == 0) {
					$( "#kuler-pages>div:first" ).html( "<br /><br />" );
				} else {
					$( "#kuler-pages>div:first" ).html( "PAGE<br />" + current_page + "/" + total_pages );
					$( "#kuler-pages #kuler-back-page" ).html( "<img src=\"" + images["larrow"] + "\" alt=\" \" />" );
					if( total_pages == 1 ) {
						$( "#kuler-pages #kuler-next-page" ).html( "<img src=\"" + images["rarrow"] + "\" alt=\" \" />" );
					} else {
						$( "#kuler-pages #kuler-next-page" ).html( "<img src=\"" + images["rarrow_light"] + "\" alt=\" \" />" );
					}
				}
				
				$( "#kuler .color-drag" ).draggable({
					appendTo: "body",
					revert: true,
					revertDuration: 200,
					opacity: 1,
					containment: "document",
					cursor: "move",
					helper: "clone",
					zIndex: 3000,
					iframeFix: true,
					drag: function() {
						TR.movingColor = 1;
					}
				});
				
				$( "#kuler .color-drag" ).mousedown(function() {
					var color = $( this ).css( "background-color" );
					TR.addMostRecent( color );
				});
				
				$( ".kuler-title" ).mouseover(function() {
					$( ".kuler-details" ).hide();
					var title = $( this );
					var top = title.offset().top;
					var bottom = top + title.height();
					var left = title.offset().left;
					var right = left + title.width();
					$( "[data-id=" + $( this ).attr( "data-id" ) + "].kuler-details" ).css({
						"top": top - 10 + "px",
						"left": right - 319 + "px"
					}).show();
				});

				$( ".kuler-title" ).mouseout(function(e) {
					var title = $( this );
					var top = title.offset().top;
					var bottom = top + title.height();
					var left = title.offset().left;
					var right = left + title.width();
					if( e.pageX < left || e.pageY > bottom || e.pageY < top) {
						$( "[data-id=" + $( this ).attr( "data-id" ) + "].kuler-details" ).hide();
					}
				});

				$( ".kuler-details" ).mouseleave(function() {
					$( this ).hide();
				});
					
			},
			error: function(e) {
				kuler.html( "<div id=\"kuler-loader\" style=\"width: 28px\">Error</div>" );
			}
		});
	}

});

